<?php	final class Scripts {		// Добавляем нового пользователя		public static function Add($params) {						$db = DB::getInstance();			$data = array(	'name' => $params['name'],							'description' => $params['description'],							'action' => $params['action']						);			$db->insert('scripts', $data);						$result = DB::getInstance()->select('SELECT max(`id`) AS `new_id` FROM `scripts`');			return $result[0]['new_id'];		}					// Редактируем пользователя		public static function Edit($params) {					$db = DB::getInstance();			$data = array(	'name' => $params['name'],							'description' => $params['description'],							'action' => $params['action']						);			$db->update('scripts', $data, 'id LIKE "'.$params['sid'].'"');			return $params['sid'];		}			// Удаляем пользователя		public static function Delete($script_id) {			$db = DB::getInstance()->query('DELETE FROM `scripts` WHERE `id` LIKE "'.$script_id.'"');			$db = DB::getInstance()->query('DELETE FROM `scriptpermissions` WHERE `id` LIKE "'.$script_id.'"');			return 'DeleteOK';		}		}?>